import React from "react";
import "./Filter.css";

const categories = [
  { label: "Decor", value: "decor" },
  { label: "Furniture", value: "furniture" },
  { label: "Storage", value: "storage" },
  { label: "Wearables", value: "wearables" },
];

const sortBy = [
  { label: "Best Sellers", value: "best-sellers" },
  { label: "Price: Low - High", value: "price-low-to-high" },
  { label: "Price: High - Low", value: "price-high-to-low" },
  { label: "Oldest to Newest", value: "oldest-to-newest" },
  { label: "Newest to Oldest", value: "newest-to-oldest" },
];

const Filter = ({ products, setFilteredProducts }) => {
  const filterProductsBy = (category) => {
    const filteredProducts = products.filter(
      (product) => product.category === category
    );
    setFilteredProducts(filteredProducts);
  };

  const sortProductsBy = (category) => {
    let sortedProducts = [];
    if(category === "best-sellers") {
      sortedProducts = products.sort((a, b) => b.numberOfItemsSold - a.numberOfItemsSold);
    }
    console.log(sortedProducts);
    setFilteredProducts(sortedProducts);
  }

  return (
    <div className="filter-wrap">
      <div className="flt-sticky-container">
        <h4 className="filter-title">Categories</h4>
        <ul className="filter">
          {categories.map((category) => (
            <li
              key={category.value}
              className="filter-ele"
              onClick={() => filterProductsBy(category.value)}
            >
              {category.label}
            </li>
          ))}
        </ul>
        <h4 className="filter-title">Sort By</h4>
        <ul className="filter">
          {sortBy.map((category) => (
            <li key={category.value} className="filter-ele" onClick={() => sortProductsBy(category.value)}>
              {category.label}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Filter;
